static void write_partition_table(const char *new_table){
    esp_err_t ret = spi_flash_erase_range(CONFIG_PARTITION_TABLE_OFFSET, 0x2000);
    if(ret == ESP_OK){
        spi_flash_write(CONFIG_PARTITION_TABLE_OFFSET, new_table, 0x2000);
        // nvs_flash_erase();
        // nvs_flash_init();
        esp_restart();
    }
}

